import Chart from "chart.js/auto";
import { CategoryScale } from "chart.js";
import { Line } from "react-chartjs-2";

Chart.register(CategoryScale);

enum TipoEstacao {
  HIDRO = "Hidro",
  METEO = "Meteo",
  PLUVIO = "Pluvio",
  BARRAGEM = "Barragem",
}

type Estacao = {
  nome?: string;
  tipo?: TipoEstacao;
  codigo?: string;
  last_read?: string;
  nivel_rio?: number;

