  chuva_003h?: number;
  chuva_006h?: number;
  chuva_012h?: number;
  chuva_024h?: number;
  chuva_048h?: number;
  chuva_072h?: number;
  chuva_096h?: number;
  chuva_120h?: number;
  chuva_144h?: number;
  chuva_168h?: number;

  temp_atual?: number;
  temp_sens?: number;
  umidade?: number;

  vel_vento?: number;
  dir_vento?: number;
  pres_atmos?: number;

  localizacao?: {
    lat: number;
    lng: number;
  };

  nivel_montante?: number;
  nivel_jusante?: number;
  porc_reservatorio?: number;
  comp_abertas?: number;
  comp_fechadas?: number;
};

type Label = {
  itajai: string[],
  blumenau: string[],
  brusque: string[]
}


