import React, { useCallback, useEffect, useState } from 'react';
import * as https from 'https';
const rp = require('request-promise');
import logo from './logo.svg';
import './App.css';
import { gql, useQuery } from '@apollo/client';
import axios from 'axios';
import { apiBlumenau } from './services/api';

export enum TipoEstacao {
  HIDRO = "Hidro",
  METEO = "Meteo",
  PLUVIO = "Pluvio",
  BARRAGEM = "Barragem",
}

export type Estacao = {
  nome?: string;
  tipo?: TipoEstacao;
  codigo?: string;
  last_read?: string;
  nivel_rio?: number;

  nivel_rio_historico?: Array<{ t_stamp: string; nivel: number }>;

  chuva_001h?: number;
  chuva_003h?: number;
  chuva_006h?: number;
  chuva_012h?: number;
  chuva_024h?: number;
  chuva_048h?: number;
  chuva_072h?: number;
  chuva_096h?: number;
  chuva_120h?: number;
  chuva_144h?: number;
  chuva_168h?: number;

  temp_atual?: number;
  temp_sens?: number;
  umidade?: number;

  vel_vento?: number;
  dir_vento?: number;
  pres_atmos?: number;

  localizacao?: {
    lat: number;
    lng: number;
  };

  nivel_montante?: number;
  nivel_jusante?: number;
  porc_reservatorio?: number;
  comp_abertas?: number;
  comp_fechadas?: number;
};

function App() {
  const [estacoes, setEstacao] = useState<Estacao[]>([])

  async function getDataBlumenau() {
    const mathRandom = Math.ceil(Math.random() * 9999999)
    //const response = await apiBlumenau.get("/static/data/nivel_oficial.json?a=7192054",{headers:{
    //  'Access-Control-Allow-Origin' : '*',
    //  'Access-Control-Allow-Methods':'GET,PUT,POST,DELETE,PATCH,OPTIONS',
    //}})
    const agent = new https.Agent({  
      rejectUnauthorized: false
    });
    const response = await axios.get(`https://alertablu.blumenau.sc.gov.br/static/data/nivel_oficial.json?a=${mathRandom}`,{headers:{
      'Access-Control-Allow-Origin' : '*',
      'Access-Control-Allow-Methods':'GET,PUT,POST,DELETE,PATCH,OPTIONS',
    },
  httpAgent:agent
  })
    console.log(response.data)
    rp("https://alertablu.blumenau.sc.gov.br/static/data/nivel_oficial.json")
  .then(function(html){
    //success!
    console.log(html);
  })
  .catch(function(err){
    //handle error
  });
    //const response = await fetch("https://alertablu.blumenau.sc.gov.br/static/data/nivel_oficial.json")
    //console.log(response)
  }

  const GET_ESTACOES = gql`
  query ListaEstacoes {
    estacoes {
      nome
      codigo
      tipo
      last_read

      localizacao {
        lat
        lng
      }

      nivel_rio
      nivel_rio_historico {
        t_stamp
        nivel
      }

      chuva_001h
      chuva_003h
      chuva_006h
      chuva_012h
      chuva_024h
      chuva_048h
      chuva_072h
      chuva_096h
      chuva_120h
      chuva_144h
      chuva_168h

      temp_atual
      temp_sens
      umidade
      vel_vento
      dir_vento
      pres_atmos

      nivel_montante
      nivel_jusante
      porc_reservatorio
      comp_abertas
      comp_fechadas
    }
  }
`;

  const updateEstacoes = (
    (data: { estacoes: Estacao[] }) => setEstacao(data.estacoes)
  );

  useQuery(GET_ESTACOES, {
    onError: () => { },
    onCompleted: updateEstacoes,
    fetchPolicy: "cache-and-network",
    pollInterval: 15000,
  });



  useEffect(() => {

    getDataBlumenau()
  }, [])


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
