import React, { useCallback, useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { gql, useQuery } from '@apollo/client';
import axios from 'axios';
import { apiBlumenau } from './services/api';
import Chart from "chart.js/auto";
import { CategoryScale } from "chart.js";
import { Line } from "react-chartjs-2";

Chart.register(CategoryScale);

export enum TipoEstacao {
  HIDRO = "Hidro",
  METEO = "Meteo",
  PLUVIO = "Pluvio",
  BARRAGEM = "Barragem",
}

export type Estacao = {
  nome?: string;
  tipo?: TipoEstacao;
  codigo?: string;
  last_read?: string;
  nivel_rio?: number;

  nivel_rio_historico?: Array<{ t_stamp: string; nivel: number }>;

  chuva_001h?: number;
  chuva_003h?: number;
  chuva_006h?: number;
  chuva_012h?: number;
  chuva_024h?: number;
  chuva_048h?: number;
  chuva_072h?: number;
  chuva_096h?: number;
  chuva_120h?: number;
  chuva_144h?: number;
  chuva_168h?: number;

  temp_atual?: number;
  temp_sens?: number;
  umidade?: number;

  vel_vento?: number;
  dir_vento?: number;
  pres_atmos?: number;

  localizacao?: {
    lat: number;
    lng: number;
  };

  nivel_montante?: number;
  nivel_jusante?: number;
  porc_reservatorio?: number;
  comp_abertas?: number;
  comp_fechadas?: number;
};

function App() {
  const [estacoes, setEstacao] = useState<Estacao[]>([])
  const [labels, setLabels] = useState<string[]>([])
  const [data, setData] = useState<number[]>([])
  async function getDataBlumenau() {
    const mathRandom = Math.ceil(Math.random() * 9999999)
    //const response = await apiBlumenau.get("/static/data/nivel_oficial.json?a=7192054",{headers:{
    //  'Access-Control-Allow-Origin' : '*',
    //  'Access-Control-Allow-Methods':'GET,PUT,POST,DELETE,PATCH,OPTIONS',
    //}})

    const response = await axios.get(`https://alertablu.blumenau.sc.gov.br/static/data/nivel_oficial.json?a=${mathRandom}`,{headers:{
      'Access-Control-Allow-Origin' : '*',
      'Access-Control-Allow-Methods':'GET,PUT,POST,DELETE,PATCH,OPTIONS',
    }
  })
    console.log(response.data)

    //const response = await fetch("https://alertablu.blumenau.sc.gov.br/static/data/nivel_oficial.json")
    //console.log(response)
  }

  const GET_ESTACOES = gql`
  query ListaEstacoes {
    estacoes {
      nome
      codigo
      tipo
      last_read

      localizacao {
        lat
        lng
      }

      nivel_rio
      nivel_rio_historico {
        t_stamp
        nivel
      }

      chuva_001h
      chuva_003h
      chuva_006h
      chuva_012h
      chuva_024h
      chuva_048h
      chuva_072h
      chuva_096h
      chuva_120h
      chuva_144h
      chuva_168h

      temp_atual
      temp_sens
      umidade
      vel_vento
      dir_vento
      pres_atmos

      nivel_montante
      nivel_jusante
      porc_reservatorio
      comp_abertas
      comp_fechadas
    }
  }
`;

  const updateEstacoes = (
    (data: { estacoes: Estacao[] }) => setEstacao(data.estacoes)
  );

  useQuery(GET_ESTACOES, {
    onError: () => { },
    onCompleted: updateEstacoes,
    fetchPolicy: "cache-and-network",
    pollInterval: 15000,
  });



  useEffect(() => {
    if(estacoes.length > 0){
      estacoes.forEach((item)=>{
        item.nivel_rio_historico?.forEach((item)=>{
          console.log("Data: ", new Date(item.t_stamp).getDate())
          console.log("Nivel: ", item.nivel)
          const date = `${new Date(item.t_stamp).getDate()}/${new Date(item.t_stamp).getMonth()}`
          labels.push(date)
          data.push(item.nivel)
        })
      })
    }
  }, [estacoes])


  return (
    <div className="App">
      <Line data={{
        labels: labels,
        datasets:[
          {
            data: data,
            label:"Nivel Brusque"
          }
        ]
      }}/>
    </div>
  );
}

export default App;
