import React, { useCallback, useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { gql, useQuery } from '@apollo/client';
import axios from 'axios';

export enum TipoEstacao {
  HIDRO = "Hidro",
  METEO = "Meteo",
  PLUVIO = "Pluvio",
  BARRAGEM = "Barragem",
}

export type Estacao = {
  nome?: string;
  tipo?: TipoEstacao;
  codigo?: string;
  last_read?: string;
  nivel_rio?: number;

  nivel_rio_historico?: Array<{ t_stamp: string; nivel: number }>;

  chuva_001h?: number;
  chuva_003h?: number;
  chuva_006h?: number;
  chuva_012h?: number;
  chuva_024h?: number;
  chuva_048h?: number;
  chuva_072h?: number;
  chuva_096h?: number;
  chuva_120h?: number;
  chuva_144h?: number;
  chuva_168h?: number;

  temp_atual?: number;
  temp_sens?: number;
  umidade?: number;

  vel_vento?: number;
  dir_vento?: number;
  pres_atmos?: number;

  localizacao?: {
    lat: number;
    lng: number;
  };

  nivel_montante?: number;
  nivel_jusante?: number;
  porc_reservatorio?: number;
  comp_abertas?: number;
  comp_fechadas?: number;
};

function App() {
  const [estacoes, setEstacao] = useState<Estacao[]>([])

  async function getDataBlumenau() {
    const mathRandom = Math.ceil(Math.random() * 9999999)
    axios.defaults.headers.common["Access-Control-Allow-Credentials"] = true //'Access-Control-Allow-Credentials', 'true'
    const response = await axios.get(`https://alertablu.blumenau.sc.gov.br/static/data/nivel_oficial.json?a=${mathRandom}`)
    console.log(response.data)
  }

  const GET_ESTACOES = gql`
  query ListaEstacoes {
    estacoes {
      nome
      codigo
      tipo
      last_read

      localizacao {
        lat
        lng
      }

      nivel_rio
      nivel_rio_historico {
        t_stamp
        nivel
      }

      chuva_001h
      chuva_003h
      chuva_006h
      chuva_012h
      chuva_024h
      chuva_048h
      chuva_072h
      chuva_096h
      chuva_120h
      chuva_144h
      chuva_168h

      temp_atual
      temp_sens
      umidade
      vel_vento
      dir_vento
      pres_atmos

      nivel_montante
      nivel_jusante
      porc_reservatorio
      comp_abertas
      comp_fechadas
    }
  }
`;

  const updateEstacoes = (
    (data: { estacoes: Estacao[] }) => setEstacao(data.estacoes)
  );

  useQuery(GET_ESTACOES, {
    onError: () => { },
    onCompleted: updateEstacoes,
    fetchPolicy: "cache-and-network",
    pollInterval: 15000,
  });



  useEffect(() => {

    getDataBlumenau()
  }, [])


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
